MsgN("Wastemod: Player custom hud loaded!")/*---------------------------------------------------------   Name: EscondeHUD( name )   Desc: Stop the drawing of the hl hud---------------------------------------------------------*/function EscondeHUD( name )	if (name == "CHudHealth" or name == "CHudBattery" or name == "CHudAmmo" or name == "CHudSecondaryAmmo") then		return false	endendhook.Add( "HUDShouldDraw", "HideAll", EscondeHUD )/*---------------------------------------------------------   Name: DrawHUD()   Desc: Draw wastemod hud---------------------------------------------------------*/function DrawHUD()		if (LocalPlayer():Alive()) then		--Scrap meter		draw.SimpleText( "Scrap: "..LocalPlayer():GetScrap(), "Trebuchet24", 15, 25, Color(255,255,255), TEXT_ALIGN_LEFT, TEXT_ALIGN_CENTER )	endendhook.Add("HUDPaint", "DrawWasteHUD", DrawHUD)